import React, { Component } from 'react'
import Slider from "react-slick";
import styles from "./carousel.module.css";

function SampleNextArrow(props) {
    const { className, style, onClick } = props;
    return (
      <div

        onClick={onClick}
        className={[styles.arrow, styles.arrowRight].join(" ")}
      >
         <span>Next</span>
      </div>
    );
  }

  function SamplePrevArrow(props) {
    const { className, style, onClick } = props;
    return (
      <div
        className={className}
        style={{ ...style, display: "block", background: "green" }}
        onClick={onClick}
      >

      </div>
    );
  }


export class carousel extends Component {



    render() {
        const settings =  {
        centerMode: true,
        centerPadding: '60px',
        slidesToShow: 3,
        nextArrow: <SampleNextArrow />,
        prevArrow: <SamplePrevArrow />,
        dots:true,
        responsive: [
          {
            breakpoint: 768,
            settings: {
              arrows: true,
              centerMode: true,
              centerPadding: '40px',
              slidesToShow: 1
            }
          },
          {
            breakpoint: 480,
            settings: {
              arrows: true,
              centerMode: true,
              centerPadding: '40px',
              slidesToShow: 1
            }
        }
        ]
    }
        return (
           <div>

        <Slider {...settings}>
          <div>
            <h3>1</h3>
          </div>
          <div>
            <h3>2</h3>
          </div>
          <div>
            <h3>3</h3>
          </div>
          <div>
            <h3>4</h3>
          </div>
          <div>
            <h3>5</h3>
          </div>
          <div>
            <h3>6</h3>
          </div>
        </Slider>
      </div>
    );

    }
}

export default carousel
