import React, { Component } from 'react';
import { useHistory, Route } from 'react-router-dom';
import "./projects.css"
import ProjectDetails from './ProjectDetails/ProjectDetails';
import { withRouter } from 'react-router'
export class SingleProject extends Component {
    state = {
        testing: "Hello"
    }

    postSelectedHandler = (id) => {
        //  this.setState({selectedPostId: id});
        this.props.history.push("/reactclockwise/projects/" + id);
      }

    render() {
        return (
            <React.Fragment>
            <div class="filter-item" >
                <div class="single-project-item">
                    <div class="img-holder">
                    <img src={this.props.projectImage} alt="Project Image" />
                    <div class="overlay-style-one">
                        <div class="box">
                        <div class="content">
                            <button onClick={() => this.postSelectedHandler(this.props.id)} >View Project</button>
                            <div class="text-holder">
                            <h4>{this.props.projectName}</h4>
                            <p>Lorem ipsum dolor sit amet.</p>
                            </div>
                        </div>
                        </div>
                    </div>
                </div>
            </div>
            </div>
            <Route path={"/reactclockwise/projects/:id"}  component={ProjectDetails}/>
            </React.Fragment>
        )
    }
}

export default withRouter(SingleProject);
