import React, { Component } from 'react'
import styles from "./slider.module.css";
import Slider from "react-slick";


export class HomeSlider extends Component {

    render() {

      const slides = document.querySelectorAll('.slide');
      const controls = document.querySelectorAll('.control');
      let activeSlide = 0;
      let prevActive = 0;

    changeSlides();
    let int = setInterval(changeSlides, 20000);

function changeSlides() {
	slides[prevActive].classList.remove('active');
	controls[prevActive].classList.remove('active');

	slides[activeSlide].classList.add('active');
	controls[activeSlide].classList.add('active');

	prevActive = activeSlide++;

	if(activeSlide >= slides.length) {
		activeSlide = 0;
	}

	console.log(prevActive, activeSlide);
}

        var settings = {
            useCSS: true,
            dots:true,
            infinite: true,
            speed: 500,
            slidesToShow: 1,
            slidesToScroll: 1
          };

        return (
          <div>
            <Slider className={styles.sliderContainer} {...settings}>
        <div >
          <h3>1</h3>
        </div>
        <div >
          <h3>2</h3>
        </div>
        <div >
          <h3>3</h3>
        </div>
      </Slider>
      <div  className={styles.controlsContainer}>
      <div data-index="1" className={styles.control}></div>
      <div data-index="2" className={styles.control}></div>
      <div data-index="3" className={styles.control}></div>
    </div>
    </div>
        )
    }
}

export default HomeSlider
